mapper.select:
select * from sys_dept where del_flag = 0

  and parent_id = #{dept.parentId}


  and dept_name like concat('%', #{dept.deptName}, '%')


  and status = #{dept.status}


${dept.params.dataScope}
order by parent_id, order_num

select d.*, (select dept_name from sys_dept where dept_id = d.parent_id) parent_name
from sys_dept d
where d.dept_id = #{deptId}

mapper.update:
update sys_dept set ancestors =

  when #{item.deptId} then #{item.ancestors}

where dept_id in

  #{item.deptId}
