mapper.select:
select distinct su.user_id, su.dept_id, su.login_name, su.user_name, su.user_type, su.email, su.avatar, su.phonenumber, su.status, su.create_time from sys_user su left join sys_dept sd on su.dept_id = sd.dept_id left join sys_user_role sur on su.user_id = sur.user_id left join sys_role sr on sr.role_id = sur.role_id where su.del_flag = 0 and sr.role_id = #{roleId}
and su.login_name like concat('%', #{user.loginName}, '%')
and su.phonenumber like concat('%', #{user.phonenumber}, '%')
${user.params.dataScope}

select distinct u.user_id, u.dept_id, u.login_name, u.user_name, u.user_type, u.email, u.avatar, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = 0 and r.role_id = #{roleId}
and u.login_name like concat('%', #{loginName}, '%')
and u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}
